'use strict';

var jwt = require('jsonwebtoken');
var BasicIdToken = require('./basicIdToken');  


function ExtendedIdToken(name, email, picture, iss, sub, aud, iat, exp, nbf){
    this.name = name;
    this.email = email;
    this.picture = picture;
    this.iss = iss;
    this.sub = sub;
    this.aud = aud;
    this.iat = iat;
    this.exp = exp;
    this.nbf = nbf;
    this.validateRequiredFields();
};

ExtendedIdToken.prototype = Object.create(BasicIdToken.prototype);
ExtendedIdToken.prototype.constructor = ExtendedIdToken;

ExtendedIdToken.prototype.options_to_payload = {
    'name': 'name',
    'email': 'email',
    'picture': 'picture',
    'iss': 'iss',
    'sub': 'sub',
    'aud': 'aud',
    'iat': 'iat',
    'exp': 'exp',
    'nbf': 'nbf',

};
  
ExtendedIdToken.prototype.options_for_objects = [
    'expiresIn',
    'notBefore',
    'noTimestamp',
    'audience',
    'issuer',
    'subject',
    'jwtid',
];

ExtendedIdToken.prototype.knownNonStandardClaims = {
    'jti' : 'jti',
};

ExtendedIdToken.prototype.validateRequiredFields = function(){
    if (this.name && this.email && this.picture && this.iss && this.sub && this.aud && this.iat && this.exp && this.nbf){
        console.log("Validated all standard fields")
    }else {
        throw new Error("You are missing a required parameter");
    }
};

ExtendedIdToken.prototype.getStandardClaims = function(){
    ExtendedIdToken.prototype.standard_claims = {"name": this.name, "email" : this.email, "picture": this.picture,  "iss" : this.iss, "sub" : this.sub, "aud" : this.aud, "iat": this.iat, "exp": this.exp, "nbf": this.nbf};
    return ExtendedIdToken.prototype.standard_claims;         
};

module.exports = ExtendedIdToken;

